sys_script_include.script:
var Reviewer = Class.create();
Reviewer.prototype = {
    initialize: function(githubToken, teamMembers) {
		this.githubToken = githubToken;
		this.teamMembers = teamMembers;
    },

	addReviewers: function(repoName, prNumber, prCreator) {
		var reviewers = this.teamMembers.filter(function(member) {
			return member !== prCreator;
		});

		if (reviewers.length === 0) {
			return;
		}
		var sm = new sn_ws.RESTMessageV2();
		var url = 'https://code-test.devsnc.com/api/v3/repos/dev/' + repoName +
				  '/pulls/' + prNumber + '/requested_reviewers?access_token=' + this.githubToken;
		sm.setEndpoint(url);
		sm.setHttpMethod('POST');
		sm.setRequestHeader('Content-Type', 'application/json');
		sm.setRequestBody(JSON.stringify({"reviewers": reviewers }));
		return sm.execute();
	},

    type: 'Reviewer'
};
